

        package com.example.songr;


import com.example.songr.Album;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.ResponseBody;


import java.util.ArrayList;
import java.util.List;

@Controller
public class HelloWorldController {
    @GetMapping("/hello")
    public String hello(){
        return "hello";
    }

    @GetMapping("/capitalize/{sentence}")
//    @ResponseBody
    public String capitalize(@PathVariable String sentence){
        return "capitalize";
    }

    @GetMapping("/album")
//    @ResponseBody
    public String album(Model model){
        ArrayList <Album> albumList = new ArrayList<>();
        albumList.add(new Album("Parachutes", "ColdPlay", 10, 2489,"https://upload.wikimedia.org/wikipedia/en/5/57/Coldplayparachutesalbumcover.jpg"));
        albumList.add(new Album("What's the Story","Oasis", 11, 3006, "https://upload.wikimedia.org/wikipedia/en/b/b1/Oasis_-_%28What%27s_The_Story%29_Morning_Glory_album_cover.jpg"));
        albumList.add(new Album("Purple Rain", "Prince", 9, 2631, "data:image/jpeg;base64,"));
        model.addAttribute("album", albumList);
        return "album";
    }
}
